var foo = require('foo');
var myFoo = new foo("Tom",40);
console.log("get the private value before update");
console.log(myFoo.getName());
console.log(myFoo.getAge());

console.log("update the private value");
myFoo.setName("Bob");
myFoo.setAge(22);

console.log("get the update value");
console.log(myFoo.getName());
console.log(myFoo.getAge());

console.log("get the public value before update");
console.log(myFoo.name);
console.log(myFoo.age);

console.log("update the public value");
myFoo.name = 'Bob';
myFoo.age = 23;

console.log("get the updated public value");
console.log(myFoo.name);
console.log(myFoo.age);

console.log(module.loaded);
console.log(module.parent);